import AppModule from './app-module.js';

class CameraModule extends AppModule {
    constructor() {
        super();

        this.addMethod('flash', '_flash');
        this.addMethod('on', '_on');
        this.addMethod('removeListener', '_removeListener');
        this.addMethod('onPictureTaken', '_onPictureTaken');
        this.addMethod('takePicture', '_takePicture');
        this.addMethod('lastPicture', '_lastPicture');
        this.addMethod('getPicture', '_getPicture');
        this.addMethod('connect', '_connect');
    }

    static get id() { return 'camera'; }

    _flash(color, length) {
        if (this.api) {
            this.api.ledring.flash(color, length);
        }
    }

    _on() {
        if (this.api) {
            this.api.on.apply(this.api, arguments);
        }
    }

    _removeListener() {
        if (this.api) {
            this.api.removeListener.apply(this.api, arguments);
        }
    }

    _onPictureTaken(cb) {
        if (this.api) {
            this._on.call(this, 'camera:takepicture', cb);
        }
    }

    _takePicture() {
        if (this.api) {
            return this.api.camera.takePicture();
        }
        return '';
    }

    _lastPicture() {
        if (this.api) {
            return this.api.camera.lastPicture();
        }
        return '';
    }

    _getPicture(filename) {
        if (this.api) {
            return this.api.camera.getPicture(filename);
        }
        return '';
    }

    _connect(info) {
        if (this.api) {
            this.api.connect();
            this.api.on('connect', () => {
                this.api.emit('camera:init', info);
            });
        }
    }

    config(opts) {
        this.api = opts.hardwareAPI;
    }
}

export default CameraModule;

import AppModule from './app-module.js';

class ColourModule extends AppModule {
    constructor() {
        super();

        this.addMethod('random', '_random');
        this.addMethod('create', '_create');
        this.addMethod('lerp', '_lerp');
    }

    static get id() { return 'colour'; }

    HSVtoRGB(h, s, v) {
        let r;
        let g;
        let b;
        let i;
        let f;
        let p;
        let q;
        let t;
        i = Math.floor(h * 6);
        f = h * 6 - i;
        p = v * (1 - s);
        q = v * (1 - f * s);
        t = v * (1 - (1 - f) * s);
        switch (i % 6) {
        case 0: r = v, g = t, b = p; break;
        case 1: r = q, g = v, b = p; break;
        case 2: r = p, g = v, b = t; break;
        case 3: r = p, g = q, b = v; break;
        case 4: r = t, g = p, b = v; break;
        case 5: r = v, g = p, b = q; break;
        }
        return {
            r: Math.round(r * 255),
            g: Math.round(g * 255),
            b: Math.round(b * 255),
        };
    }

    RGBtoHEX(r, g, b) {
        const hexColor = ((1 << 24) + (r << 16) + (g << 8) + b).toString(16).substr(1);
        return `#${hexColor}`;
    }

    HEXtoRGBComponents(hex) {
        const match = hex.replace(/#/, '').match(/.{1,2}/g);
        return {
            r: parseInt(match[0], 16),
            g: parseInt(match[1], 16),
            b: parseInt(match[2], 16),
        };
    }

    _random() {
        const num = Math.floor(Math.random() * Math.pow(2, 24));
        return `#${(`00000${num.toString(16)}`).substr(-6)}`;
    }

    _luminance(hex, lum) {
        let rgb = '#',
            c,
            i;
        // validate hex string
        hex = String(hex).replace(/[^0-9a-f]/gi, '');
        if (hex.length < 6) {
            hex = hex[0] + hex[0] + hex[1] + hex[1] + hex[2] + hex[2];
        }
        lum = lum || 0;

        // convert to decimal and change luminosity
        for (i = 0; i < 3; i++) {
            c = parseInt(hex.substr(i * 2, 2), 16);
            c = Math.round(Math.min(Math.max(0, c + (c * lum)), 255)).toString(16);
            rgb += (`00${  c}`).substr(c.length);
        }

        return rgb;
    }

    _create(type, one, two, three) {
        if (type === 'rgb') {
            let red,
                green,
                blue;
            one = Math.min(100, one);
            two = Math.min(100, two);
            three = Math.min(100, three);
            red = Math.round(one * 2.55);
            green = Math.round(two * 2.55);
            blue = Math.round(three * 2.55);
            return this.RGBtoHEX(red, green, blue);
        } else if (type === 'hsv') {
            let hue,
                saturation,
                value,
                rgb;
            one %= 100;
            two = Math.min(100, two);
            three = Math.min(100, three);
            hue = one / 100;
            saturation = two / 100;
            value = three / 100;
            rgb = this.HSVtoRGB(hue, saturation, value);
            return this.RGBtoHEX(rgb.r, rgb.g, rgb.b);
        }
    }

    _lerp(from, to, percentage) {
        from = this.HEXtoRGBComponents(from);
        to = this.HEXtoRGBComponents(to);
        percentage = Math.min(Math.max(percentage, 0), 100);
        const components = ['r', 'g', 'b'],
            newColor = {};
        for (let i = 0; i < components.length; i++) {
            const c = components[i];
            newColor[c] = Math.round(from[c] + (to[c] - from[c]) * percentage / 100);
        }
        return this.RGBtoHEX(newColor.r, newColor.g, newColor.b);
    }
}

export default ColourModule;

import AppModule from './app-module.js';

const THROTTLE_DELAY = 1000;

const emojiMap = {};

emojiMap['01d'] = '\u2600\uFE0F';
emojiMap['02d'] = '\uD83C\uDF24';
emojiMap['03d'] = '\uD83C\uDF25';
emojiMap['04d'] = '\u2601\uFE0F';
emojiMap['09d'] = '\uD83C\uDF27';
emojiMap['10d'] = '\uD83C\uDF26';
emojiMap['11d'] = '\u26C8 ';
emojiMap['13d'] = '\uD83C\uDF28';
emojiMap['50d'] = '\uD83C\uDF2B';

class DataModule extends AppModule {
    constructor() {
        super();
        this.cache = {};

        this.addMethod('generateRequest', '_generateRequest');

        this._kano = {};
        this._kano.getShares = (id) => this.get(id, this.fetchData(this.API_URL + "/share"))
                .then(r => r.json())
                .then(data => {
                    return data.entries.map(share => {
                        return {
                            title: share.title,
                            likes: share.likes.length,
                            user: share.user.username,
                            image: share.cover_url
                        };
                    });
                });

        this.addMethod('kano', '_kano');

        this._weather = {};
        this._weather.getWeather = (id, config) => {
            let body = {
                    q: config.location,
                    units: config.units,
                },
                url = this.buildUrl('weather-city', body);
            return this.get(id, this.fetchData(url))
                .then(r => r.json())
                .then((data) => {
                    let weather = data.weather[0],
                        icon = weather ? weather.icon : '01d';
                    // Force daily emojis
                    icon = icon.replace('n', 'd');
                    return {
                        temperature: data.main.temp,
                        wind_speed: data.wind.speed,
                        wind_angle: data.wind.deg,
                        clouds: data.clouds.all,
                        emoji: emojiMap[icon] || '\u2600\uFE0F',
                        isSunny: (weather.id === 800),
                        isRainy: (weather.id >= 500 && weather.id < 600),
                        isSnowy: (weather.id >= 600 && weather.id < 700),
                        isCloudy: (weather.id >= 801 && weather.id < 900),
                    };
                });
        };
        this._weather.is = (status, data) => {
            switch (status) {
            case 'sunny': {
                return data.isSunny;
            }
            case 'rainy': {
                return data.isRainy;
            }
            case 'snowy': {
                return data.isSnowy;
            }
            case 'cloudy': {
                return data.isCloudy;
            }
            }
        };

        this.addMethod('weather', '_weather');

        this._space = {};
        this._space.getISSStatus = (id, config) => {
            const url = this.buildUrl('iss');
            return this.get(id, this.fetchData(url))
                .then(r => r.json());
        };

        this.addMethod('space', '_space');

        this._rss = {};
        this._rss.getFeed = (id, config) => {
            let body = { src: config.src },
                url = this.buildUrl('rss', body);
            return this.get(id, this.fetchData(url))
                .then(r => r.json())
                .then((data) => data.slice(0, 10));
        };

        this.addMethod('rss', '_rss');

        this._sports = {};
        this._sports.getResults = (id, config) => {
            let body = { src: config.src },
                url = this.buildUrl('rss-sports', body);
            return this.get(id, this.fetchData(url))
                .then(r => r.json())
                .then((data) => data.slice(0, 10));
        };

        this.addMethod('sports', '_sports');

        this._clock = {};
        this._clock.noop = (id, config) => Promise.resolve();

        this.addMethod('clock', '_clock');
    }

    static get id() { return 'data'; }

    config(c) {
        super.config(c);
        this.API_URL = c.API_URL;
        this.DATA_API_URL = c.DATA_API_URL;
    }

    get(id, fetchImpl) {
        // Ignore errors here to support K2 server
        try {
            const event = new Event('request');
            event.detail = {
                id,
            };
            document.dispatchEvent(event);
        } catch (e) {}
        return fetchImpl
            .then((r) => {
                try {
                    const event = new Event('response');
                    event.detail = {
                        id,
                        res: r.clone(),
                    };
                    document.dispatchEvent(event);
                } catch (e) {}
                return r;
            }).catch((e) => {});
    }

    buildUrl(srcId, queryParams) {
        let baseUrl = `${this.DATA_API_URL  }/data-src/${  srcId  }/`,
            queryString = '';

        if (queryParams) {
            queryString = Object.keys(queryParams).reduce((prev, key) => {
                const pair = `${key  }=${  encodeURIComponent(queryParams[key])}`;

                if (prev.length) {
                    prev += '&';
                } else {
                    prev += '?';
                }

                return prev + pair;
            }, '');
        }

        return baseUrl + queryString;
    }

    /**
     * A wrapper around the fetch API for our data proxy.
     */
    fetchData(url) {
        let currentDate = Date.now();

        // The responses are caches with a timestamp of the request, the cached response is returned
        // if the THROTTLE_DELAY time hasn't passed between the previous call and this one
        if (!this.cache[url] || currentDate - this.cache[url].timestamp > THROTTLE_DELAY) {
            let promise = fetch(url)
                .then((r) => {
                    return r.json().then((d) => {
                        let s,
                            init = {"status" : r.status, "statusText" : r.statusText},
                            response;

                        if (d.success && d.value) {
                            s = JSON.stringify(d.value);
                        }  else if (d.entries){
                            // for shares data
                            s = JSON.stringify(d);
                        }  else {
                            console.error('Failed to retrieve data');
                            s = '{}';
                        }

                        response = new Response(s, init);

                        this.cache[url].response = response.clone();

                        return response;
                    }).catch(e => {});
                }).catch(e => {});
            this.cache[url] = {
                timestamp: currentDate,
                finished: promise
            };
            return promise;
        } else {
            if (this.cache[url].response) {
                // Returns a cloned version of the response
                return Promise.resolve(this.cache[url].response.clone());
            }
                // Wait for the request to finish before returning the response
                return this.cache[url].finished.then(() => {
                    return this.cache[url].response.clone()
                }).catch(e => {});

        }
    }

    _generateRequest(id, methodPath, config) {
        let pieces = methodPath.split('.'),
            method = this.methods;
        pieces.forEach((key) => {
            method = method[key];
        });
        return method(id, config);
    }
}

export default DataModule;

