    }
    localize(...args) {
        return this.rootEl.localize(...args);
    }
    setEditor(editor) {
        this.editor = editor;
    }
    initializeChallenge() {
        const { config } = this.editor;
        const challenge = this.store.getState();

        const { steps } = (challenge.scene || challenge);

        if (steps) {
            setTimeout(() => {
                this.engine.setSteps(steps);
                this.engine.start();
