import VM from '../vm.js';
import AppModulesLoader from '../app-modules/index.js';
import { Plugin } from './plugin.js';

export class Runner extends Plugin {
    constructor() {
        super();
        this.modules = [];
        this._onRunningStateChange = this._onRunningStateChange.bind(this);
    }
    addModule(mod) {
        const mods = Array.isArray(mod) ? mod : [mod];
        mods.forEach(m => this.modules.push(m));
        this._updateModules();
    }
    onInstall(output) {
        this.output = output;
        this._updateModules();
        this.output.on('running-state-changed', this._onRunningStateChange);
    }
    onImport() {
        // Force refresh app state if was running
        const running = this.output.getRunningState();
        if (running) {
            this.output.setRunningState(!running);
            this.output.setRunningState(running);
        }
    }
    _updateModules() {
        if (!this.output) {
            return;
        }
        if (this.appModulesLoader) {
            const { appModules } = this.appModulesLoader;
            appModules.stop();
        }
        this.appModulesLoader = new AppModulesLoader(this.output, this.modules);
        this.appModulesLoader.start();
    }
    _onRunningStateChange() {
        const running = this.output.getRunningState();
        const { appModules } = this.appModulesLoader;
        if (!running) {
            appModules.stop();
            return;
        }
        const code = this.output.getCode();
        // Generate the code
        const appCode = appModules.createAppCode('AppModules', code);
        // Start all the modules. Will also trigger the `start` event from `global`
        appModules.start();
        // Prepare a sandbox exposing AppModules
        const vm = new VM({ AppModules: appModules });
        // Run the code
        vm.runInContext(appCode);
        appModules.afterRun();
    }
    instrumentize(method) {
        const { appModules } = this.appModulesLoader;
        return appModules.instrumentize(method);
    }
    dispose() {
        const { appModules } = this.appModulesLoader;
        appModules.stop();
    }
}

export default Runner;

import { PolymerElement } from '@polymer/polymer/polymer-element.js';
let defined = false;

class StoreObserver {
    constructor(store, editor) {
        if (!defined) {
            class Observer extends store.StateReceiver(PolymerElement) {
                static get is() { return 'kc-editor-observer'; }
                static get properties() {
                    return {
                        addedParts: {
                            linkState: 'addedParts',
                        },
                    };
                }
                static get observers() {
                    return [
                        '_addedPartsSplices(addedParts.splices)',
                    ];
                }
                _addedPartsSplices(changes) {
                    if (!changes) {
                        return;
                    }
                    changes.indexSplices.forEach((splice) => {
                        splice.removed.forEach((part) => {
                            this.editor.trigger('part-removed', part);
                        });
                        for (let i = 0; i < splice.addedCount; i += 1) {
                            const index = splice.index + i;
                            const part = splice.object[index];
                            this.editor.trigger('part-added', part);
                        }
                    });
                }
            }
            customElements.define(Observer.is, Observer);
            defined = true;
        }
        this.observerEl = document.createElement('kc-editor-observer');
        this.observerEl.editor = editor;
    }
    get rootEl() {
        return this.observerEl;
    }
}

export default StoreObserver;

import { PolymerElement } from '@polymer/polymer/polymer-element.js';
import GlobalStore from '../global-store.js';

const Store = {
    create(initState) {
        const store = GlobalStore.create(initState);

        class StoreElement extends store.StateProvider(PolymerElement) {
            static get is() { return `kc-store-${store.id}`; }
        }

        customElements.define(StoreElement.is, StoreElement);

        const storeElement = new StoreElement();

        store.providerElement = storeElement;

        return store;
    },
    types(constants) {
        return Object.freeze(constants.reduce((acc, constant) => {
            acc[constant] = constant;
            return acc;
        }, {}));
    },
};

export default Store;

import { Plugin } from './plugin.js';
import MetaModule from '../meta-api/module.js';
// TODO: Only load the required renderer for the source type
import BlocklyMetaRenderer from '../meta-api/renderer/blockly.js';
import TypeScriptMetaRenderer from '../meta-api/renderer/typescript.js';

class Entry {
    constructor(toolbox, entry) {
        this.toolbox = toolbox;
        this.entry = entry;
    }
    dispose() {
        this.toolbox.removeEntry(this.entry);
    }
    update(entry) {
        this.toolbox.updateEntry(this.entry, entry);
        this.entry = entry;
    }
}

class Toolbox extends Plugin {
    constructor() {
        super();
        this.entries = [];
    }
    onInstall(editor) {
        this.editor = editor;
        switch (editor.sourceType) {
        case 'code': {
            this.renderer = new TypeScriptMetaRenderer();
            break;
        }
        default:
        case 'blockly': {
            this.renderer = new BlocklyMetaRenderer();
            break;
        }
        }
    }
    setEntries(entries) {
        this.entries = entries;
    }
    addEntry(entry, position) {
        const injectIndex = typeof position === 'undefined' ? this.entries.length : position;
        const disposableEntry = new Entry(this, entry);
        this.entries.splice(injectIndex, 0, entry);
        this.update();
        return disposableEntry;
    }
    removeEntry(entry) {
        const index = this.entries.indexOf(entry);
        this.entries.splice(index, 1);
        this.update();
    }
    updateEntry(oldEntry, entry) {
        const index = this.entries.indexOf(oldEntry);
        this.entries.splice(index, 1, entry);
        this.update();
    }
    setWhitelist(whitelist) {
        this.whitelist = whitelist;
    }
    update() {
        // Not injected yet, let the inject callback generate the toolbox
        if (!this.editor.injected) {
            return;
        }
        this.onInject();
    }
    onInject() {
        const toolbox = this.entries
            .map(entry => this.renderer.renderToolboxEntry(new MetaModule(entry), this.whitelist))
            .filter(entry => entry);
        this.editor.editorActions.setToolbox(toolbox);
    }
}

export default Toolbox;

