import { TelemetryClient } from '@kano/telemetry/index.js';
import { Plugin } from '../plugin.js';
import { Subscriptions, subscribe } from '../../util/subscription.js';

/**
 * Manages the actions in the toolbar avilable in the workspace
 */
export class WorkspaceToolbar extends Plugin {
    constructor() {
        super();
        this.subscriptions = new Subscriptions();
        this._telemetry = new TelemetryClient({ scope: 'workspace_toolbar' });
    }
    /**
     * @param {Editor} editor The editor this plugin is attached to
     */
    onInstall(editor) {
        /** @type {Editor} */
        this.editor = editor;
        this.editor.telemetry.mount(this._telemetry);
    }
    onInject() {
        const { workspaceView } = this.editor;
        this.toolbar = workspaceView.toolbar;
        if (!this.toolbar) {
            return;
        }

        this.subscriptions.push(
            subscribe(this.editor.output, 'running-state-changed', this.updateRunningState.bind(this)),
            subscribe(this.editor.output, 'fullscreen-changed', this.updateFullscreen.bind(this)),
            subscribe(this.toolbar, 'restart-clicked', this.restart.bind(this)),
            subscribe(this.toolbar, 'run-clicked', this.toggleRun.bind(this)),
            subscribe(this.toolbar, 'fullscreen-clicked', this.toggleFullscreen.bind(this)),
            subscribe(this.toolbar, 'reset-clicked', this.reset.bind(this)),
            subscribe(this.toolbar, 'export-clicked', this.export.bind(this)),
            subscribe(this.toolbar, 'import-clicked', this.import.bind(this)),
        );
    }
    updateRunningState() {
        this.toolbar.running = this.editor.output.getRunningState();
    }
    updateFullscreen() {
        this.toolbar.fullscreen = this.editor.output.getFullscreen();
    }
    restart() {
        this.editor.output.restart();
        this._telemetry.trackEvent({ name: 'restart_clicked' });
    }
    toggleRun() {
        this.editor.output.toggleRunningState();
        this._telemetry.trackEvent({ name: 'run_clicked' });
    }
    toggleFullscreen() {
        this.editor.output.toggleFullscreen();
        this._telemetry.trackEvent({ name: 'fullscreen_clicked', properties: { value: this.editor.output.getFullscreen() } });
    }
    reset() {
        this.editor.reset();
        this._telemetry.trackEvent({ name: 'reset_clicked' });
    }
    export() {
        this.editor.exportToDisk();
        this._telemetry.trackEvent({ name: 'export_clicked' });
    }
    import() {
        this.editor.importFromDisk();
        this._telemetry.trackEvent({ name: 'import_clicked' });
    }
    addEntry(...args) {
        if (!this.toolbar) {
            return WorkspaceToolbar.createDisposableNoop();
        }
        return this.toolbar.addEntry(...args);
    }
    addSettingsEntry(...args) {
        if (!this.toolbar) {
            return WorkspaceToolbar.createDisposableNoop();
        }
        return this.toolbar.addSettingsEntry(...args);
    }
    static createDisposableNoop() {
        return {
            on() {},
            updateTitle() {},
            updateIcon() {},
            updateIronIcon() {},
            dispose() {},
        };
    }
    onDispose() {
        this.subscriptions.dispose();
    }
}


export default WorkspaceToolbar;

import { GifWriter } from './omggif/import.js';

export class GifEncoder {
    constructor(opts) {
        this.generating = false;

        this.lastJobId = 0;

        this.workerUrl = opts.workerUrl || GifEncoder.WORKER_URL;

        this.width = Math.floor(opts.width);
        this.height = Math.floor(opts.height);
        this.length = opts.length;
        this.workerPoolSize = opts.workerPoolSize || window.navigator.hardwareConcurrency;

        this.queue = [];
        this.frames = [];

        this.workerPool = [];
        for (let i = 0; i < this.workerPoolSize; i += 1) {
            this.workerPool.push({
                worker: new Worker(this.workerUrl),
                inUse: false,
            });
        }
    }

    static get WORKER_URL() {
        return '/libs/QuantWorker.js';
    }

    addFrame(context, delay) {
        // Add the frame to the queue
        this.queue.push({ context, delay, i: this.frames.length });
        // Keep a slot for the frame
        this.frames.push(null);
        this.tick();
    }

    tick() {
        if (!this.queue.length) {
            // Don't end here if there are frames left to render
            for (let i = 0; i < this.frames.length; i += 1) {
                if (!this.frames[i]) {
                    return;
                }
            }
            if (typeof this.finishedQueue === 'function') {
                this.finishedQueue();
                this.finishedQueue = null;
            }
            return;
        }
        const nextInQueue = this.queue[0];
        for (let i = 0; i < this.workerPool.length; i += 1) {
            if (!this.workerPool[i].inUse) {
                this.workerPool[i].inUse = true;
                this.renderFrame(
                    nextInQueue.context,
                    nextInQueue.delay,
                    nextInQueue.i,
                    this.workerPool[i].worker,
                )
                    .then(() => {
                        this.workerPool[i].inUse = false;
                        this.tick();
                    });
                this.queue.shift();
                return;
            }
        }
    }

    renderFrame(context, delay, i, worker) {
        return new Promise((resolve, reject) => {
            const frameContext = context.getImageData(0, 0, this.width, this.height);
            const frameData = frameContext.data;
            const frameJobId = i;

            const onCompletion = (e) => {
                if (e.data.job_id === frameJobId) {
                    const { palette, pixels_for_gif } = e.data;
                    this.frames[i] = {
                        pixels: pixels_for_gif,
                        duration: e.data.job_duration,
                        palette,
                        delay,
                    };
                    worker.removeEventListener('message', onCompletion);
                    resolve();
                }
            };

            worker.addEventListener('message', onCompletion);

            worker.postMessage({
                job_id: frameJobId,
                width: this.width,
                height: this.height,
                frameData,
            });
        });
    }

    end() {
        return new Promise((resolve) => {
            this.finishedQueue = () => {
                let string = '';

                this.buffer = new Uint8Array(this.width * this.height * this.frames.length);
                this.gif = new GifWriter(this.buffer, this.width, this.height, { loop: 0 });

                this.frames.forEach((frame) => {
                    this.gif.addFrame(0, 0, this.width, this.height, frame.pixels, {
                        palette: new Uint32Array(frame.palette),
                        delay: frame.delay / 10,
                        disposal: 1,
                    });
                });

                this.stats = this.frames.reduce((acc, frame) => {
                    acc.frames.push(frame.duration);
                    acc.total += frame.duration;
                    return acc;
                }, { total: 0, frames: [] });

                const buffer = new Uint8Array(this.gif.end());

                for (let i = 0, l = this.gif.end(); i < l; i += 1) {
                    string += String.fromCharCode(this.buffer[i]);
                    buffer[i] = this.buffer[i];
                }

                this.data_url = `data:image/gif;base64,${window.btoa(string)}`;

                this.generating = false;
                return resolve(new Blob([buffer], { type: 'image/gif' }));
            };
            this.tick();
        });
    }
}

export default { GifEncoder };

