export class Canvas {
    constructor(settings) {
        this.session = {};
        this.general = new General(this.session);
        this.palette = Palette;
        this.paths = new Paths(this.session);
        this.setters = new Setters(this.session);
        this.shapes = new Shapes(this.session);
        this.space = new Space(this.session);
        this.text = new Text(this.session);
        this.reset(settings);
    }
    reset(settings) {
        this.session.ctx = settings.ctx;
        this.session.width = settings.width;
        this.session.height = settings.height;
        this.session.ratio = settings.ratio || 1;
        this.session.pos = {
            x: settings.width / 2,
            y: settings.height / 2,
        };
        this.general.reset();
        this.space.moveTo(this.session.pos.x, this.session.pos.y);
        this.setters.strokeColor(this.session.settings.stroke.color);
        this.setters.strokeWidth(this.session.settings.stroke.width);
        this.paths.lineCap(this.session.settings.stroke.cap);
        this.text.updateFont();
    }
}

export default Canvas;

import { Utils } from '../utils.js';

function General(session) {
    this.session = session;
}

/*
* Clear the stage from current drawing
*
* @return void
*/
General.prototype.clear = function clear() {
    let width = this.session.width * this.session.ratio,
        height = this.session.height * this.session.ratio;

    this.session.ctx.clearRect(0, 0, width, height);

    if (this.session.settings.bg) {
        this.background(this.session.settings.bg);
    }
};

/*
* Set a background color
*
* @param {String} color
* @return void
*/
General.prototype.background = function background(color) {
    let width = this.session.width * this.session.ratio,
        height = this.session.height * this.session.ratio;

    color = Utils.parseColor(color);
    this.session.ctx.globalCompositeOperation = 'destination-over';

    this.session.settings.bg = color;
    this.session.ctx.fillStyle = this.session.settings.bg;
    this.session.ctx.beginPath();
    this.session.ctx.rect(0, 0, width, height);
    this.session.ctx.closePath();
    this.session.ctx.fill();
    this.session.ctx.globalCompositeOperation = 'source-over';
};

/*
* Reset this.session settings to default and clear the stage
*
* @return void
*/
General.prototype.reset = function () {
    this.session.pos = Utils.getCenter(this.session);

    this.session.settings = {
        bg: null,
        fill: '#333',
        stroke: {
            width: 0,
            color: '#222',
            cap: 'butt',
        },
        text: {
            size: 25,
            font: 'Bariol',
            align: 'center',
            bold: false,
            italic: false,
            baseline: 'alphabetic',
        },
    };

    this.clear();
};

/*
* Get a random number between two numbers, rounded to an integer unless
* float attribute is set to true
*
* @param {Number}* min
* @param {Number}* max
* @param {Boolena}* float
* @return {Number}
*/
General.prototype.random = function (min, max, float) {
    let out;

    if (typeof min !== 'number' || typeof max !== 'number') {
        return Math.random();
    }

    out = Math.random() * (max - min) + min;

    if (float) {
        return out;
    }

    return Math.floor(out);
};

export { General };

import { Space } from './space.js';
import { Utils } from '../utils.js';

function Paths(session) {
    this.session = session;
    this.space = new Space(this.session);
}

/*
* Draw a line from current cursor position to absolute x and y coordinates
*
* @param {Number} x
* @param {Number} y
* @return void
*/
Paths.prototype.lineTo = function (x, y) {
    var ratio = this.session.ratio,
    pos = Utils.parseCoordinates(this.session, x, y),
    from = {
        x : this.session.pos.x,
        y : this.session.pos.y
    };

    x = pos.x;
    y = pos.y;

    this.space.moveTo(this.session.pos.x, this.session.pos.y, false);
    Utils.startShape(this.session);
    this.session.ctx.moveTo(from.x * ratio, from.y * ratio);
    this.session.ctx.lineTo(x * ratio, y * ratio);
    Utils.endShape(this.session);
};

/*
* Draw a line from current cursor position to relative x and y coordinates
*
* @param {Number} x
* @param {Number}* y
* @return void
*/
Paths.prototype.line = function (x, y) {
    y = y || 0;

    this.lineTo(this.session.pos.x + x, this.session.pos.y + y);
};

/*
* Set linecap to given type
*
* @param {String} type
* @return void
*/
Paths.prototype.lineCap = function (type) {
    this.session.ctx.lineCap = type;
};

export { Paths };

import { Utils } from '../utils.js';

function Setters(session) {
    this.session = session;
}

/*
* Set current this.session stroke color
*
* @param {String} color
* @return void
*/
Setters.prototype.strokeColor = function (color) {
    color = Utils.parseColor(color);
    this.session.settings.stroke.color = color;
    this.session.ctx.strokeStyle = color;
};

/*
* Set current this.session stroke width
*
* @param {Number} val
* @return void
*/
Setters.prototype.strokeWidth = function (val) {
    this.session.settings.stroke.width = val;
    this.session.ctx.lineWidth = val * this.session.ratio;
};

/*
* Set current this.session mixed stroke attributes
*
* @param {*...} attributes
* @return void
*/
Setters.prototype.stroke = function () {
    var style = Utils.parseLineStyle(arguments);
    if (style.color) {
        this.strokeColor(style.color);
    }
    if (typeof style.width !== 'undefined') {
        this.strokeWidth(style.width);
    }
};

/*
* Set current this.session fill color
*
* @param {String} color
* @return void
*/
Setters.prototype.color = function (val) {
    val = Utils.parseColor(val);
    this.session.settings.fill = val || 'transparent';
};

export { Setters };

import { Utils } from '../utils.js';

function Shapes(session) {
    this.session = session;
}

/*
* Draw a rectangle using current cursor position as origin
*
* @param {Number} width
* @param {Number} height
* @return void
*/
Shapes.prototype.rectangle = function (width, height) {
    var x, y;
    Utils.startShape(this.session);

    x = this.session.pos.x * this.session.ratio;
    y = this.session.pos.y * this.session.ratio;

    width *= this.session.ratio;
    height *= this.session.ratio;

    this.session.ctx.rect(x, y, width, height);
    Utils.endShape(this.session);
};

/*
* Draw a square using current cursor position as origin
*
* @param {Number} size
* @return void
*/
Shapes.prototype.square = function (size) {
    this.rectangle(size, size);
};

/*
* Base function to draw an arc using current cursor position as origin, rx
* and ry as x and y radiuses, start and end values ranging from 0 to 2,
* close back for path creation.
* This function is used as a base for other functions in this module
*
* @param {Number} rx
* @param {Number} ry
* @param {Number} start
* @param {Number} end
* @param {Boolean} close
* @param {Boolean} back
* @return void
*/
Shapes.prototype.drawArc = function (rx, ry, start, end, close, back) {
    var x, y, startingAngle, endingAngle;
    Utils.startShape(this.session);

    x = this.session.pos.x * this.session.ratio;
    y = this.session.pos.y * this.session.ratio;

    rx *= this.session.ratio;
    ry *= this.session.ratio;

    startingAngle = start * Math.PI;
    endingAngle = end * Math.PI; // 360 degrees is equal to 2 radians

    this.session.ctx.save();
    this.session.ctx.translate(x, y);
    this.session.ctx.scale(rx, ry);
    this.session.ctx.arc(0, 0, 1, startingAngle, endingAngle, -1);
    this.session.ctx.restore();

    Utils.endShape(this.session, close, back);
};

/*
* Draw an ellipse using current cursor position as origin
*
* @param {Number} rx
* @param {Number} ry
* @return void
*/
Shapes.prototype.ellipse = function (rx, ry) {
    this.drawArc(rx, ry, 0, 2);
};

/*
* Simplified wrapper function to draw an arc
*
* @param {Number} radius
* @param {Number} start
* @param {Number} end
* @param {Boolean} close
* @return void
*/
Shapes.prototype.arc = function (radius, start, end, close) {
    this.drawArc(radius, radius, start, end, close || false, close || false);
};

/*
* Draw a circle using current cursor position as origin
*
* @param {Number} radius
* @return void
*/
Shapes.prototype.circle = function (radius) {
    this.ellipse(radius, radius);
};

/*
* Draw a polygon using every two arguments as coordinates for a new point
*
* @param {Number} radius
* @return void
*/
Shapes.prototype.polygon = function () {
    var lastArg = arguments[arguments.length - 1],
        close = lastArg === true || lastArg === false ? lastArg : false,
        baseX = this.session.pos.x * this.session.ratio,
        baseY = this.session.pos.y * this.session.ratio,
        points = [],
        i;

    Utils.startShape(this.session);
    this.session.ctx.save();
    this.session.ctx.lineTo(baseX, baseY);

    points.push({ x: 0, y: 0 });

    for (i = 0; i < arguments.length; i += 2) {
        this.session.ctx.lineTo(
            baseX + arguments[i] * this.session.ratio,
            baseY + arguments[i + 1] * this.session.ratio
        );
        points.push({ x: arguments[i], y: arguments[i + 1] });
    }
    this.session.ctx.restore();
    Utils.endShape(this.session, close);
};

/*
* Draw a single pixel with no stroke using current cursor position as origin
*
* @return void
*/
Shapes.prototype.pixel = function () {
    var x = this.session.pos.x * this.session.ratio,
        y = this.session.pos.y * this.session.ratio;
    this.session.ctx.save();
    this.session.ctx.lineWidth = 0;
    this.session.ctx.fillStyle = this.session.settings.fill;
    this.session.ctx.fillRect(x, y, 1, 1);
    this.session.ctx.restore();
};

export { Shapes };

import { Utils } from '../utils.js';

function Space(session) {
    this.session = session;
}

/*
* Move cursor to absolute x and y positions
*
* @param {Number} x
* @param {Number} y
* @return void
*/
Space.prototype.moveTo = function (x, y) {
    var pos, dx, dy;
    x = x || 0;
    y = y || 0;

    pos = Utils.parseCoordinates(this.session, x, y);
    dx = pos.x - this.session.pos.x;
    dy = pos.y - this.session.pos.y;

    x = pos.x;
    y = pos.y;

    this.session.pos = { x: x, y: y };

    x = this.session.pos.x * this.session.ratio;
    y = this.session.pos.y * this.session.ratio;


    this.session.ctx.moveTo(x, y);
};

/*
* Move cursor to random x and y positions
*
* @return void
*/
Space.prototype.moveToRandom = function () {
    var x, y;
    x = Math.floor(Math.random() * this.session.width);
    y = Math.floor(Math.random() * this.session.height);
    this.moveTo(x,y);
};

/*
* Move cursor by relative x and y amounts
*
* @param {Number} x
* @param {Number} y
* @return void
*/
Space.prototype.move = function (x, y) {
    y = y || 0;
    this.moveTo(this.session.pos.x + x, this.session.pos.y + y);
};

export { Space };

function Text(session) {
    this.session = session;
}

/*
* Set updated font with new key / val setting
*
* @param {String} key
* @param {*} val
* @return void
*/
Text.prototype.updateFont = function (key, val) {
    var size,
    format;
    if (key) {
        this.session.settings.text[key] = val;
    }

    size = this.session.settings.text.size * this.session.ratio;
    format = '';

    if (this.session.settings.text.italic) {
        format += 'italic ';
    }
    if (this.session.settings.text.bold) {
        format += 'bold ';
    }

    this.session.ctx.font = format + size + 'px ' + this.session.settings.text.font;
    this.session.ctx.textAlign = this.session.settings.text.align;
    this.session.ctx.textBaseline = this.session.settings.text.baseline;
};

/*
* Set bold state (Defaults to true)
*
* @param {Boolean} state
* @return void
*/
Text.prototype.bold = function (state) {
    state = typeof state === 'undefined' ? true : state;
    this.updateFont('bold', state);
};

/*
* Set italic state (Defaults to true)
*
* @param {Boolean} state
* @return void
*/
Text.prototype.italic = function (state) {
    state = typeof state === 'undefined' ? true : state;
    this.updateFont('italic', state);
};

/*
* Set text size
*
* @param {Number} size
* @return void
*/
Text.prototype.textSize = function (size) {
    this.updateFont('size', size);
};

/*
* Set font family
*
* @param {String} font
* @return void
*/
Text.prototype.fontFamily = function (font) {
    this.updateFont('font', font);
};

/*
* Set text alignment
*
* @param {String} alignment
* @return void
*/
Text.prototype.textAlign = function (alignment) {
    this.updateFont('align', alignment);
};

/*
* Set text baseline mode
*
* @param {String} baseline
* @return void
*/
Text.prototype.textBaseline = function (baseline) {
    baseline = baseline || 'alphabetic';
    this.updateFont('baseline', baseline);
};

/*
* Set font mixed attributes
*
* @param {*..} attributes
* @return void
*/
Text.prototype.font = function () {
    var val, i;

    for (i = 0; i < arguments.length; i += 1) {
        val = arguments[i];
        if (typeof val === 'number') {
            this.textSize(val);
        } else if (val === 'left', val === 'right', val === 'center') {
            this.textAlign(val);
        } else if (typeof val === 'string') {
            this.fontFamily(val);
        }
    }
};

/*
* Draw text with current cursor position as origin and current text settings
*
* @param {String} font
* @return void
*/
Text.prototype.text = function (val) {
    var x = this.session.pos.x * this.session.ratio,
        y = this.session.pos.y * this.session.ratio;

    this.session.ctx.fillStyle = this.session.settings.fill;
    this.session.ctx.fillText(val, x, y);
};

export { Text };

import { Palette } from './modules/palette.js';
export const Utils = {};

/*
* Begin a vector shape path on current context
*
* @return void
*/
Utils.startShape = function (session) {
    session.ctx.beginPath();
};

/*
* End a vector shape path on current context, close and bring back to origin
*
* if specified
* @param {Boolean} close
* @param {Boolean} back
* @return void
*/
Utils.endShape = function (session, close, back) {
    close = typeof close === 'undefined' ? true : close;

    if (back) {
        session.ctx.lineTo(session.pos.x * session.ratio, session.pos.y * session.ratio);
    }

    if (close) {
        session.ctx.closePath();
    }

    if (session.settings.stroke.width) {
        session.ctx.stroke();
    }

    session.ctx.fillStyle = session.settings.fill;
    session.ctx.fill();
};

/*
* Parse stroke attributes from string and return stroke settings object
*
* @param {Boolean} close
* @return {Object}
*/
Utils.parseLineStyle = function (attributes) {
    var out = {},
    i, attr;

    for (i = 0 ; i < attributes.length; i += 1) {
        attr = attributes[i];

        if (typeof attr === 'number') {
            out.width = attr;
        } else {
            out.color = attr;
        }
    }

    return out;
};

/*
* Parse or evaluate a coordinate from string or number
*
* @param {String|Number} val
* @param {String} type
* @return {Number}
*/
Utils.parseCoordinate = function (session, val, type) {
    type = type || 'x';

    if (typeof val === 'string') {
        if (type === 'x') {
            switch (val) {
                case 'center':
                    return Utils.getCenter().x;
                case 'right':
                    return session.width;
                case 'left':
                    return 0;
            }
        } else if (type === 'y') {
            switch (val) {
                case 'center':
                    return Utils.getCenter().y;
                case 'bottom':
                    return session.height;
                case 'top':
                    return 0;
            }
        }
    }

    return val;
};

/*
* Parse a pair of x / y coordinates
*
* @param {String|Number} x
* @param {String|Number} y
* @return {Object}
*/
Utils.parseCoordinates = function (session, x, y) {
    x = Utils.parseCoordinate(session, x, 'x');
    y = Utils.parseCoordinate(session, y, 'y');

    return { x: x, y: y };
};

/*
* Get stage center in current session
*
* @return {Object}
*/
Utils.getCenter = function (session) {
    return {
        x : session.width / 2,
        y : session.height / 2
    };
};

/*
* Get a color from palette if it's an existing key or just return
*
* @param {String} val
* @return void
*/
Utils.parseColor = function (val) {
    return Palette[val] || val;
};

/*
* Given value is a valid color
*
* @param {*} val
* @return {Boolean}
*/
Utils.isColorValue = function (val) {
    if (typeof val !== 'string') {
        return false;
    }

    if (val.substr(0, 1) === '#' && val.length > 3 && val.length <= 7) {
        return true;
    } else if (Palette[val]) {
        return true;
    }

    return false;
};

