import { LEVEL, LEVEL_NAMES, LEVEL_STYLES } from './level.js';

class Logger {
    constructor() {
        this.level = LEVEL.OFF;
    }
    setLevel(level) {
        this.level = level;
    }
    log(...args) {
        if (this.level > LEVEL.OFF) {
            console.log(`%c[${LEVEL_NAMES[this.level]}]`, LEVEL_STYLES[this.level], ...args);
        }
    }
    trace(...args) {
        if (this.level <= LEVEL.TRACE) {
            this.log(...args);
        }
    }
    debug(...args) {
        if (this.level <= LEVEL.DEBUG) {
            this.log(...args);
        }
    }
    info(...args) {
        if (this.level <= LEVEL.INFO) {
            this.log(...args);
        }
    }
    warn(...args) {
        if (this.level <= LEVEL.WARN) {
            console.warn(...args);
        }
    }
    error(...args) {
        if (this.level <= LEVEL.ERROR) {
            console.error(...args);
        }
    }
}

export { Logger, LEVEL };
export default Logger;

const LEVEL = {
    TRACE: 0,
    DEBUG: 10,
    INFO: 20,
    WARN: 30,
    ERROR: 40,
    OFF: -1,
};

const LEVEL_NAMES = {
    [LEVEL.TRACE]: 'TRACE',
    [LEVEL.DEBUG]: 'DEBUG',
    [LEVEL.INFO]: 'INFO',
    [LEVEL.WARN]: 'WARN',
    [LEVEL.ERROR]: 'ERROR',
    [LEVEL.OFF]: 'OFF',
};

const LEVEL_STYLES = {
    [LEVEL.TRACE]: 'color: lightgrey',
    [LEVEL.DEBUG]: 'color: green',
    [LEVEL.INFO]: 'color: cyan',
    [LEVEL.WARN]: 'color: orange',
    [LEVEL.ERROR]: 'color: red',
    [LEVEL.OFF]: 'color: transparent',
};

export default LEVEL;
export { LEVEL, LEVEL_NAMES, LEVEL_STYLES };

class Meta {
    constructor(def, parent) {
        this.parent = parent;
        this.def = def;
        this.build();
    }
    build() {
        if (!this.def.symbols) {
            return;
        }
        this.symbols = this.def.symbols.map((sym) => {
            switch (sym.type) {
            case 'variable': {
                return new MetaVariable(sym, this);
            }
            case 'module': {
                return new MetaModule(sym, this);
            }
            case 'function': {
                return new MetaFunction(sym, this);
            }
            default: {
                break;
            }
            }
            return null;
        }).filter(v => v);
    }
    getNameChain(sep = '_', prev = '') {
        if (!this.parent) {
            return this.def.name;
        }
        return `${this.parent.getNameChain(sep, prev)}${sep}${this.def.name}`;
    }
    getColor() {
        if (this.def.color) {
            return this.def.color;
        }
        if (this.parent) {
            return this.parent.getColor();
        }
        return '';
    }
    getVerboseDisplay() {
        if (typeof this.def.verbose === 'undefined') {
            return this.def.name;
        }
        return this.def.verbose;
    }
}

class MetaParameter extends Meta {
    getReturnType() {
        return this.def.returnType;
    }
}

class MetaVariable extends Meta {
    getReturnType() {
        return this.def.returnType;
    }
}

class MetaModule extends Meta {

}

class MetaFunction extends Meta {
    constructor(...args) {
        super(...args);
        this.parameters = (this.def.parameters || []).map(p => new MetaParameter(p, this));
    }
    getReturnType() {
        return this.def.returnType;
    }
    getParameters() {
        return this.parameters;
    }
}

