                this.jsonInit(json);
            },
        };

        Blockly.JavaScript.repeat_x_times = (block) => {
            const n = Blockly.JavaScript.valueToCode(block, 'N') || 2;
            let branch = Blockly.JavaScript.statementToCode(block, 'DO');
