        name: Blockly.Msg.CATEGORY_CONTROL,
        id: ID,
        colour: COLOR,
        blocks: [
            {
                id: 'repeat_x_times',
                defaults: ['N'],
            },
            'loop_forever',
            {
                id: 'every_x_seconds',
                defaults: ['INTERVAL'],
            },
            {
                id: 'in_x_time',
                defaults: ['DELAY'],
            },
            'restart_code',
        ],
    },
    defaults: {
        every_x_seconds: {
            INTERVAL: 1,
            UNIT: 'seconds',
        },
        in_x_time: {
            DELAY: 1,
            UNIT: 'seconds',
        },
        repeat_x_times: {
            N: 10,
        },
    },
};

