                    MULTIPLY: '%{BKY_MATH_MULTIPLICATION_SYMBOL} multiply',
                    DIVIDE: '%{BKY_MATH_DIVISION_SYMBOL} divide',
                    POWER: '%{BKY_MATH_POWER_SYMBOL} to the power of',
