        /* --- max(x, y) */
        Blockly.Blocks.math_max = {
            init() {
                const json = {
                    id: 'math_max',
                    colour: COLOR,
                    message0: 'max %1 %2',
                    args0: [{
                        type: 'input_value',
                        name: 'ARG1',
                        check: 'Number',
                    }, {
                        type: 'input_value',
                        name: 'ARG2',
                        check: 'Number',
                    }],
                    inputsInline: true,
                    output: 'Number',
                };
                this.jsonInit(json);
            },
        };

        Blockly.JavaScript.math_max = (block) => {
            let arg1 = Blockly.JavaScript.valueToCode(block, 'ARG1') || 0,
                arg2 = Blockly.JavaScript.valueToCode(block, 'ARG2') || 0,
                code = `Math.max(${arg1}, ${arg2})`;
            return [code];
        };

        /* --- min(x, y) */
        Blockly.Blocks.math_min = {
            init() {
                const json = {
                    id: 'math_min',
                    colour: COLOR,
                    message0: 'min %1 %2',
                    args0: [{
                        type: 'input_value',
                        name: 'ARG1',
