                    }],
                    inputsInline: true,
                    output: 'Number',
                };
                this.jsonInit(json);
            },
        };

        Blockly.JavaScript.math_min = (block) => {
            let arg1 = Blockly.JavaScript.valueToCode(block, 'ARG1') || 0,
                arg2 = Blockly.JavaScript.valueToCode(block, 'ARG2') || 0,
                code = `Math.min(${arg1}, ${arg2})`;
            return [code];
