        /* --- sign(x) */
        Blockly.Blocks.math_sign = {
            init() {
                const json = {
                    id: 'math_sign',
                    colour: COLOR,
                    message0: 'sign %1',
                    args0: [{
                        type: 'input_value',
                        name: 'ARG',
