                    }],
                    inputsInline: true,
                    output: 'Number',
                };
                this.jsonInit(json);
            },
        };

        Blockly.JavaScript.math_sign = (block) => {
            let arg = Blockly.JavaScript.valueToCode(block, 'ARG'),
                code = `math.sign(${arg}})`;
            return [code];
        };

        /* --- random(min, max) */
        Blockly.Blocks.math_random = {
            init() {
                const json = {
                    id: 'math_random',
                    colour: COLOR,
                    message0: 'random number from %1 to %2',
                    args0: [{
                        type: 'input_value',
                        name: 'MIN',
                        check: 'Number',
