/**
* Check if a block is the ancestor of another one
*/
function checkAncestor(block, type) {
    let parent = block.parentBlock_;
    if (!parent) {
        return false;
    }
    if (parent.type == type) {
        return true;
    }
    return checkAncestor(parent, type);
}

