const lang = i18n.getLang();

const container = document.getElementById('go-in-there');
const prev = document.getElementById('prev');
const next = document.getElementById('next');

const creations = [
    'hopping-with-oscillators',
    'blue-bear_3',
    'hungry-dog',
    'tie-dye-3',
    'depth-paint',
    'too-many-bouncy-balls',
    '70s-wallpaper',
    'simple-beat-machine',
    'cells_4',
    'light-paint',
    'fluid-mixing',
    'pixel-stacker',
    'happy-easter_22',
];

let player;

i18n.load(lang, { blockly: true, kanoCodePath: '/' })
    .then(() => {
        let selected = 0;
        prev.addEventListener('click', () => {
            selected = Math.max(0, selected - 1);
            loadCreation(creations[selected]);
        });
        next.addEventListener('click', () => {
            selected = Math.min(creations.length - 1, selected + 1);
            loadCreation(creations[selected]);
        });

        document.body.style.maxWidth = '800px';

        loadCreation(creations[selected]);
    });


function loadCreation(slug) {
    if (player) {
        player.dispose();
    }
    player = new code.Player();

    player.inject(container);

    return fetch(`http://api.kano.me/share/slug/${slug}`)
        .then((r => r.json()))
        .then((body) => fetch(body.item.attachments.workspace_info_url))
        .then((r => r.json()))
        .then((contents) => {
            return player.load(contents)
                .then(() => {
                    player.setRunningState(true);
                });
        });
}